mapper.select:

select 

from LAW_OUTCOMING_RET
where OUT_ID = #{outId,jdbcType=DECIMAL}
  and TARGET_SYS_CODE = #{targetSysCode,jdbcType=VARCHAR}

mapper.delete:

delete from LAW_OUTCOMING_RET
where OUT_ID = #{outId,jdbcType=DECIMAL}
  and TARGET_SYS_CODE = #{targetSysCode,jdbcType=VARCHAR}

mapper.update:

update LAW_OUTCOMING_RET
set INTF_CODE = #{intfCode,jdbcType=VARCHAR},
  STATUS = #{status,jdbcType=VARCHAR},
  ERROR_MSG = #{errorMsg,jdbcType=VARCHAR},
  CREATION_DATE = #{creationDate,jdbcType=TIMESTAMP},
  LAST_UPDATE_DATE = #{lastUpdateDate,jdbcType=TIMESTAMP}
where OUT_ID = #{outId,jdbcType=DECIMAL}
  and TARGET_SYS_CODE = #{targetSysCode,jdbcType=VARCHAR}
